	            class SetUrls:UrlController{
					public SetUrls():base(){}
					// Create the root url (https://you path/)
					[UrlInfo("/")]
					public string MainUrl(){
						return getRootHtml();
					}
					// Special keys like Method must be in CamelCase
					[UrlInfo("/register_user/",@"{
							'Method':'Get',
							'Content-Type':'text/html; charset=utf-8'
						}")
					]
					public string UrlRegisterUser(string name,string value){
						return (registerUser(name,value))?"
Ok":"
Error";
					}
				}
