class ExampleClass:UrlController{
	public ExampleClass():base(){}
	// Create the root url (https://you path/)
	[UrlInfo("/")]
	public string MainUrl(){
		return getRootHtml();
	}
	// Special keys like Method must be in CamelCase
	// /register_user/?name=user&value=password
	[UrlInfo("/register_user/",@"{
			'Method':'Get',
			'Content-Type':'text/html; charset=utf-8'
		}")
	]
	public string UrlRegisterUser(string name,string password){
		return (registerUser(name,value))?"<h1>Ok</h1>":"<h1>Error</h1>";
	}
}
